      console.log('File selected:', file);
      uploadedFileName = file.name;
      fileUploadBox.innerHTML = `<img src="https://s3.amazonaws.com/com.voiceflow.studio/share/upload/upload.gif" alt="Uploading..." width="50" height="50">`;

